mapper.update:
update `t_student_class`
set class_name = #{className,jdbcType=VARCHAR},
  department_id = #{department.departmentId,jdbcType=INTEGER},
  grade_id = #{grade.gradeId,jdbcType=INTEGER},
  major_id = #{major.majorId,jdbcType=INTEGER}
where class_id = #{classId,jdbcType=INTEGER}

mapper.select:
SELECT t_student_class.class_id, t_student_class.class_name, t_grade.grade_id, t_grade.grade_name, t_major.major_id, t_major.major_name, t_department.department_id, t_department.department_name FROM t_student_class INNER JOIN t_grade ON t_student_class.grade_id = t_grade.grade_id INNER JOIN t_major ON t_student_class.major_id = t_major.major_id INNER JOIN t_department ON t_student_class.department_id = t_department.department_id where
t_student_class.department_id=#{departmentId,jdbcType=INTEGER}
and t_student_class.grade_id=#{gradeId,jdbcType=INTEGER}
and t_student_class.major_id=#{majorId,jdbcType=INTEGER}
